import React from "react";
import { connect } from "react-redux";
import { Field, reduxForm } from "redux-form";

import { fetchStream, editStream } from "../../actions";
import StreamForm from "./StreamForm";

class StreamEdit extends React.Component {
  componentDidMount() {
    this.props.fetchStream(this.props.match.params.id);
  }

  onSubmit(formValues) {
    this.props.editStream(this.props.match.params.id, formValues);
  }

  render() {
    if(!this.props.stream) {
      return <div>Loading...</div>;
    }

    return (
      <div>
      <h2>{this.props.stream.title}</h2>
      <p>{this.props.stream.description}</p>
      <form onSubmit={this.props.handleSubmit(this.onSubmit)}>
        <input type="text" value={this.props.stream.title} onChange={this.handleChangeTitle}></input>
        <input type="text" value={this.props.stream.description} onChange={this.handleChangeDescription}></input>
        <button>Submit</button>
      </form>
      </div>
    );
  }
};

const mapStateToProps = (state, ownProps) => {
  console.log(ownProps);
  return { stream: state.streamReducer[ownProps.match.params.id] };
};

export default connect(mapStateToProps, { fetchStream, editStream })(StreamEdit);
